mapper.select:
SELECT * FROM notes 
WHERE user_id = #{userId} AND status = #{status}
ORDER BY created_at DESC

SELECT * FROM notes 
WHERE source = #{source} AND status = #{status}
ORDER BY created_at DESC

SELECT * FROM notes 
WHERE user_id = #{userId} AND source = #{source} AND status = #{status}
ORDER BY created_at DESC

SELECT * FROM notes 
WHERE created_at BETWEEN #{start} AND #{end} AND status = #{status}
ORDER BY created_at DESC

SELECT * FROM notes 
WHERE user_id = #{userId} AND created_at BETWEEN #{start} AND #{end} AND status = #{status}
ORDER BY created_at DESC
